import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
    selector: 'app-zoom-pan',
    templateUrl: 'chart.component.html',
})
export class ZoomableChartComponent implements OnInit {
    constructor(private httpClient: HttpClient) {}

    async ngOnInit() {
        await this.fetchChartData();
    }
    async fetchChartData() {
        // Replace 'YOUR_API_ENDPOINT' with the actual API endpoint you want to fetch data from
        const response : any = await this.httpClient
            .get('https://localhost:7054/Ram')
            .toPromise();

        // Assuming the API response is an array of data objects with a 'size' property
        const dps = response.map((item: any) => ({
            y: item.size,
        }));

        // Update the chart data with the fetched data in 'dps' format
        this.chartOptions.data[0].dataPoints = dps;
        console.log(this.chartOptions.data[0].dataPoints);
        return dps;


    }
    generateRandomData = () => {
        var y = 1000, dps = [];
        for (var i = 0; i < 100; i++) {
            y += Math.round(Math.random() * 10 - 5);
            dps.push({y: y});
        }
        return dps;
    }
    chartOptions = {
        zoomEnabled: true,
        animationEnabled: true,
        exportEnabled: true,
        theme: 'light2',
        title: {
            text: 'Try Zooming & Panning',
        },
        data: [
            {
                type: 'line',
                dataPoints: this.generateRandomData(),
            },
        ],
    };


}
