import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
    selector: 'app-zoom-pan',
    templateUrl: 'chart.component.html',
})
export class ZoomableChartComponent implements OnInit {
    constructor(private httpClient: HttpClient) {}

    async ngOnInit() {
        await this.fetchChartData();
    }

    chartOptions = {
        zoomEnabled: true,
        animationEnabled: true,
        exportEnabled: true,
        theme: 'light2',
        title: {
            text: 'Try Zooming & Panning',
        },
        data: [
            {
                type: 'line',
                dataPoints: null,
            },
        ],
    };

    fetchChartData() {
        // Replace 'https://localhost:7054/Ram' with your actual API endpoint
        this.httpClient.get('https://localhost:7054/Ram').subscribe((response: any) => {
            // Process the API response and assign data points
            const dataPoints = response.map((item: any) => ({
                y: item.size,
                x: item.xValue, // You may need to adjust this based on your API response structure
            }));

            // Assign the data points to the chartOptions
            this.chartOptions.data[0].dataPoints = dataPoints;

            // Log the updated dataPoints
            console.log(this.chartOptions.data[0].dataPoints);
        });
    }
}
